// -------------------------------------------------------- 
// Normalize Search Message -> Funcionalidad:
// Este nodo unifica la informaci√≥n recibida desde Teams (AdaptiveCard)
// con metadatos globales de la conversaci√≥n y normaliza el texto enviado 
// por el usuario para extraer rol, ubicaci√≥n y tecnolog√≠as.
// --------------------------------------------------------

// --------------------------------------------------------
// Objetivo:
// 1. Generar metadatos est√°ndar (request_id, timestamp, source).
// 2. Detectar mensaje enviado por el usuario desde la AdaptiveCard.
// 3. Limpiar el texto (quitar markdown).
// 4. Extraer rol, ubicaci√≥n y skills mediante regex o fallback.
// 5. Retornar un objeto JSON en snake_case con todos los datos listos
//    para la b√∫squeda de candidatos.
// --------------------------------------------------------


// --------------------------------------------------------
// Funci√≥n para generar un request_id √∫nico (sin paquete externo)
// --------------------------------------------------------
function generateRequestId() {
  return (
    'req_' + Date.now().toString(36) + '_' + Math.random().toString(36).substring(2, 10)
  );
}

// --------------------------------------------------------
// Obtener input desde el nodo anterior
// --------------------------------------------------------
const input = items[0].json;
const data = Array.isArray(input) ? input[0] : input;

// --------------------------------------------------------
// Inicializar campos b√°sicos
// --------------------------------------------------------
let raw = '';
let senderId = null;
let userId = null;
let userName = null;
let context = null;
let source = 'unknown';

// Timestamp ISO
const timestamp = new Date().toISOString();

// --------------------------------------------------------
// Detectar origen de datos
// --------------------------------------------------------
if (data.fields && data.fields.searchMessage) {
  // Caso Teams -> AdaptiveCard con Input.Text
  raw = data.fields.searchMessage;
  senderId = data.user_id || null;
  userId = data.user_id || null;
  userName = data.user_name || null;
  context = data.action || null;
  source = 'teams';
} else if (data.chatInput) {
  // Caso input manual desde chat n8n_dev (testing)
  raw = data.chatInput;
  senderId = data.sessionId || null;
  userId = data.sessionId || null;
  userName = 'n8n_dev_user';
  context = data.action || null;
  source = 'n8n_dev';
} else {
  // Fallback gen√©rico
  raw = data.text || '';
  userId = data.user_id || null;
  userName = data.user_name || null;
  context = data.action || null;
  source = 'unknown';
}

// --------------------------------------------------------
// Limpiar texto (quitar negrillas de markdown **)
// --------------------------------------------------------
const text = raw.replace(/\*{2}/g, '').trim();

// --------------------------------------------------------
// Extracci√≥n de rol, ubicaci√≥n y skills con regex
// --------------------------------------------------------
const re =
  /buscando\s+candidatos?\s+para\s+(.+?)\s+que\s+est(√©|e)n\s+(?:ubicados?|localizados?)\s+en\s+(.+?),(?:\s+y\s+)?(?:que\s+cuenten\s+con\s+conocimientos\s+t√©cnicos|con\s+experiencia)\s+en\s+(.+?)(?:\.|$)/i;

let role = '';
let location = '';
let skills = [];

const m = text.match(re);
if (m) {
  role = m[1].trim();
  location = m[2].trim();
  skills = m[3]
    .split(',')
    .map((s) => s.trim())
    .filter(Boolean);
} else {
  // Fallback simple si el patr√≥n no calza exacto
  const afterPara = text.split(/para/i)[1] || '';
  role = afterPara.split(/que\s+est[√©e]n|en\s/i)[0]?.trim() || '';

  const locMatch = text.match(/en\s+([A-Za-z√Å√â√ç√ì√ö√ú√ë√°√©√≠√≥√∫√º√±\s]+?)(?:,|\sy\s|\.|$)/i);
  location = locMatch?.[1]?.trim() || '';

  const techMatch = text.match(/en\s+([A-Za-z0-9\.\-\s,\/\+]+)(?:\.|$)/i);
  if (techMatch) {
    skills = techMatch[1]
      .split(',')
      .map((s) => s.trim())
      .filter(Boolean);
  }
}

// --------------------------------------------------------
// Construir metadatos globales
// --------------------------------------------------------
const meta = {
  request_id: generateRequestId(),
  date: timestamp.split('T')[0], // AAAA-MM-DD
  timestamp,
  source,
};

// --------------------------------------------------------
// Retornar estructura unificada en snake_case
// --------------------------------------------------------
return [
  {
    json: {
      raw,
      text,
      role,
      location,
      skills,
      sender_id: senderId,
      user_id: userId,
      user_name: userName,
      context,
      timestamp,
      meta,
      // Metadatos de Teams si aplica
      conversation_id: data.conversation_id || null,
      service_url: data.service_url || null,
      tenant_id: data.tenant_id || null,
      locale: data.locale || null,
      action: data.action || null,
    },
  },
];

